// Fun√ß√£o para dividir texto em chunks
function createChunks(text, maxChunkSize = 1500) {
  if (!text || text.length === 0) {
    return [{
      chunk_text: 'Conte√∫do vazio ou n√£o dispon√≠vel',
      chunk_index: 0,
      chunk_size: 0
    }];
  }

  const chunks = [];
  const sentences = text.split(/[.!?]+\s+/);
  let currentChunk = '';
  let chunkIndex = 0;

  for (let sentence of sentences) {
    sentence = sentence.trim();
    if (!sentence) continue;

    // Se adicionar a senten√ßa ultrapassar o limite, finaliza o chunk atual
    if (currentChunk.length + sentence.length + 2 > maxChunkSize && currentChunk.length > 0) {
      chunks.push({
        chunk_text: currentChunk.trim(),
        chunk_index: chunkIndex,
        chunk_size: currentChunk.trim().length
      });

      currentChunk = sentence + '. ';
      chunkIndex++;
    } else {
