  if (currentChunk.trim().length > 0) {
    chunks.push({
      chunk_text: currentChunk.trim(),
      chunk_index: chunkIndex,
      chunk_size: currentChunk.trim().length
    });
  }

  return chunks.length > 0 ? chunks : [{
    chunk_text: text.substring(0, maxChunkSize),
    chunk_index: 0,
    chunk_size: Math.min(text.length, maxChunkSize)
  }];
}

