// Extrai e formata a an√°lise de PDF para envio no WhatsApp
// Para usar no n8n: cole este c√≥digo no n√≥ Code ap√≥s a resposta da API OpenAI

function formatarMensagemWhatsApp() {
    try {
        // Extrai o JSON da resposta da API OpenAI
        const responseData = $('OpenAI - An√°lise PDF1').item.json;

        // Caminho para o texto da an√°lise: output[0].content[0].text
        let analiseTexto;

        if (responseData.output && responseData.output[0] && responseData.output[0].content && responseData.output[0].content[0]) {
            analiseTexto = responseData.output[0].content[0].text;
        } else {
            throw new Error("Estrutura de resposta da API n√£o encontrada");
        }

        // Parse do JSON da an√°lise
        const analise = JSON.parse(analiseTexto);

        // Verifica se o documento foi aceito ou rejeitado
        if (analise.status !== "aceito") {
            return formatarDocumentoRejeitado(analise);
        }

        // Formata mensagem completa para documento aceito
        return formatarDocumentoAceito(analise);

    } catch (error) {
        return `‚ùå *ERRO NO PROCESSAMENTO*\n\nN√£o foi poss√≠vel processar a an√°lise do documento.\n\n*Detalhes do erro:* ${error.message}`;
    }
}

function formatarDocumentoRejeitado(analise) {
    return `‚ùå *DOCUMENTO N√ÉO ANALISADO*\n` +
           `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n` +
           `üìÑ *Tipo identificado:* ${analise.tipo_documento.replace('_', ' ')}\n\n` +
           `‚ö†Ô∏è *Motivo:* ${analise.resposta_whatsapp.texto_formatado}\n\n` +
           `‚ÑπÔ∏è *Este tipo de documento n√£o √© analisado automaticamente pelo sistema.*`;
}

function formatarDocumentoAceito(analise) {
    const { resposta_whatsapp, analise_detalhada, filtros_aplicaveis } = analise;

    let mensagem = "";

    // Cabe√ßalho principal
    mensagem += `${resposta_whatsapp.emoji_categoria} *AN√ÅLISE COMPLETA DE DOCUMENTO*\n`;
    mensagem += `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n`;

    // T√≠tulo e autor
    mensagem += `üìÑ *T√çTULO:*\n${analise_detalhada.titulo}\n\n`;
    mensagem += `üë§ *AUTOR(ES):*\n${analise_detalhada.autor}\n\n`;

    // Resumo executivo
    mensagem += `üìã *RESUMO EXECUTIVO:*\n${analise_detalhada.resumo_executivo}\n\n`;

    // Insights principais
    mensagem += `üí° *PRINCIPAIS INSIGHTS:*\n`;
    analise_detalhada.insights_principais.forEach((insight, index) => {
        mensagem += `${index + 1}. ${insight}\n`;
    });
    mensagem += `\n`;

    // Dados relevantes
    mensagem += `üìä *DADOS E N√öMEROS IMPORTANTES:*\n`;
    analise_detalhada.dados_relevantes.forEach((dado) => {
        mensagem += `‚Ä¢ ${dado}\n`;
    });
    mensagem += `\n`;

    // Conclus√µes e recomenda√ß√µes
    mensagem += `üéØ *CONCLUS√ïES E RECOMENDA√á√ïES:*\n`;
    analise_detalhada.conclusoes.forEach((conclusao, index) => {
        mensagem += `${index + 1}. ${conclusao}\n`;
    });
    mensagem += `\n`;

    // Informa√ß√µes t√©cnicas
    mensagem += `‚ÑπÔ∏è *INFORMA√á√ïES T√âCNICAS:*\n`;
    mensagem += `‚Ä¢ *Tipo:* ${analise.tipo_documento.replace('_', ' ')}\n`;
    mensagem += `‚Ä¢ *Complexidade:* ${filtros_aplicaveis.complexidade.toUpperCase()}\n`;
    mensagem += `‚Ä¢ *Tamanho:* ${filtros_aplicaveis.tamanho.toUpperCase()}\n`;
    mensagem += `‚Ä¢ *Confian√ßa da classifica√ß√£o:* ${(analise.confianca_classificacao * 100).toFixed(0)}%\n\n`;

    // Tags
    if (filtros_aplicaveis.tags_automaticas && filtros_aplicaveis.tags_automaticas.length > 0) {
        mensagem += `üè∑Ô∏è *TAGS:*\n`;
        mensagem += filtros_aplicaveis.tags_automaticas
            .map(tag => `#${tag.replace(/\s+/g, '')}`)
            .join(' ');
    }

    return mensagem;
}

// Executa a fun√ß√£o e retorna o resultado
const mensagemFormatada = formatarMensagemWhatsApp();

