value.full_content:
={{(() => {
  const analise = $('Processar Resposta - Análise PDF').first().json.analise_detalhada;
  const dados = $('Processar Resposta - Análise PDF').first().json.dados_embedding;

  if (!analise) return 'Conteúdo não disponível';

  let content = `TÍTULO: ${analise.titulo || 'Sem título'}\n`;
  content += `AUTOR: ${analise.autor || 'Não informado'}\n`;
  content += `RESUMO: ${analise.resumo_executivo || ''}\n\n`;

  if (analise.insights_principais && analise.insights_principais.length > 0) {
    content += `INSIGHTS PRINCIPAIS:\n`;
    content += analise.insights_principais.map(i => `• ${i}`).join('\n') + '\n\n';
  }

  if (analise.conclusoes && analise.conclusoes.length > 0) {
    content += `CONCLUSÕES:\n`;
    content += analise.conclusoes.map(c => `• ${c}`).join('\n') + '\n\n';
  }

  if (analise.dados_relevantes && analise.dados_relevantes.length > 0) {
    content += `DADOS RELEVANTES:\n`;
    content += analise.dados_relevantes.map(d => `• ${d}`).join('\n') + '\n\n';
  }

  if (dados && dados.trechos_importantes && dados.trechos_importantes.length > 0) {
    content += `TRECHOS IMPORTANTES:\n`;
    content += dados.trechos_importantes.map(t => `Página ${t.pagina || 'N/A'}: ${t.texto}`).join('\n');
  }

  return content;
})() }}

