// Processar chunks do texto para RAG
const postgresResult = $('INSERT content_analysis (PostgreSQL)').first().json.result;
const originalData = $('Salvar mensagem e preparar para RAG').first().json;

// Extrair o conteúdo completo
const fullContent = originalData.full_content || '';
const contentAnalysisId = postgresResult.content_analysis_id;
const whatsappMessageId = postgresResult.whatsapp_message_id;

// Extrair informações específicas do conteúdo
const contentType = originalData.content_type;
const videoTitle = originalData.normalized_metadata?.video_title || originalData.normalized_metadata?.title;
const channelName = originalData.normalized_metadata?.channel_name || originalData.normalized_metadata?.author;
const mediaUrl = originalData.media_url;

// Função robusta para dividir texto em chunks com sobreposição
function createChunksWithOverlap(text, maxChunkSize = 1500, overlapSize = 200) {
  if (!text || text.length === 0) {
    return [{
      chunk_text: 'Conteúdo vazio ou não disponível',
      chunk_index: 0,
      chunk_size: 0,
      has_overlap: false
    }];
  }

  const chunks = [];
  let chunkIndex = 0;
  let startPosition = 0;

  // Se o texto é menor que o limite, retorna como um único chunk
  if (text.length <= maxChunkSize) {
    return [{
      chunk_text: text,
      chunk_index: 0,
      chunk_size: text.length,
      has_overlap: false
    }];
  }

  while (startPosition < text.length) {
    // Calcular fim do chunk atual
    let endPosition = Math.min(startPosition + maxChunkSize, text.length);

    // Se não é o último chunk, tentar encontrar uma quebra natural
    if (endPosition < text.length) {
      // Procurar por quebra de parágrafo próxima
      let paragraphBreak = text.lastIndexOf('\n\n', endPosition);
      if (paragraphBreak > startPosition && (endPosition - paragraphBreak) < 300) {
        endPosition = paragraphBreak;
      } else {
        // Procurar por quebra de linha
        let lineBreak = text.lastIndexOf('\n', endPosition);
        if (lineBreak > startPosition && (endPosition - lineBreak) < 200) {
          endPosition = lineBreak;
        } else {
          // Procurar por fim de sentença
          let sentenceBreak = text.lastIndexOf('.', endPosition);
          if (sentenceBreak > startPosition && (endPosition - sentenceBreak) < 100) {
            endPosition = sentenceBreak + 1;
          } else {
            // Procurar por espaço mais próximo
            let spaceBreak = text.lastIndexOf(' ', endPosition);
            if (spaceBreak > startPosition) {
              endPosition = spaceBreak;
            }
          }
        }
      }
    }

    // Extrair o texto do chunk
    let chunkText = text.substring(startPosition, endPosition).trim();

    // Adicionar sobreposição do chunk anterior (exceto para o primeiro chunk)
    let overlapText = '';
    let hasOverlap = false;

    if (chunkIndex > 0 && chunks.length > 0) {
      const previousChunk = chunks[chunks.length - 1].chunk_text;

      // Pegar últimas palavras do chunk anterior para sobreposição
      const words = previousChunk.split(/\s+/);
      let overlapWords = [];
      let currentOverlapSize = 0;

      // Adicionar palavras do final do chunk anterior até atingir o tamanho de overlap
      for (let i = words.length - 1; i >= 0; i--) {
        const word = words[i];
        if (currentOverlapSize + word.length + 1 <= overlapSize) {
          overlapWords.unshift(word);
          currentOverlapSize += word.length + 1;
        } else {
          break;
        }
      }

      if (overlapWords.length > 0) {
        overlapText = '...' + overlapWords.join(' ') + '\n\n';
        hasOverlap = true;
      }
    }

    // Texto final do chunk com sobreposição
    const finalChunkText = overlapText + chunkText;

    chunks.push({
      chunk_text: finalChunkText,
      chunk_index: chunkIndex,
      chunk_size: finalChunkText.length,
      has_overlap: hasOverlap,
      overlap_size: overlapText.length,
      original_size: chunkText.length
    });

    // Calcular próxima posição inicial
    if (endPosition >= text.length) {
      break;
    }

    startPosition = endPosition;

    // Evitar loop infinito
    if (startPosition === endPosition) {
      startPosition++;
    }

    chunkIndex++;
  }

  return chunks;
}

// Criar cabeçalho padrão para referência
const createChunkHeader = (chunkIndex, totalChunks, messageId, analysisId, contentType, videoInfo, hasOverlap) => {
  let header = `[CHUNK ${chunkIndex + 1}/${totalChunks}]`;

  // Indicar se tem sobreposição
  if (hasOverlap) {
    header += ` [OVERLAP]`;
  }

  header += ` [msg_id:${messageId}] [fulltext_origin_id:${analysisId}]\n`;

  // Adicionar informações específicas do vídeo se for conteúdo de vídeo
  if (contentType === 'video' && videoInfo) {
    header += `[VÍDEO] ${videoInfo.title}\n`;
    header += `[CANAL] ${videoInfo.channel}\n`;
    header += `[LINK] ${videoInfo.url}\n`;
  }

  header += `\n`;
  return header;
};

// Processar chunks com sobreposição
const chunks = createChunksWithOverlap(fullContent, 1500, 200);
const totalChunks = chunks.length;

// Log para debug
console.log(`Texto original: ${fullContent.length} caracteres`);
console.log(`Total de chunks criados: ${totalChunks}`);
console.log(`Chunks com sobreposição: ${chunks.filter(c => c.has_overlap).length}`);

// Preparar informações do vídeo para o cabeçalho
const videoInfo = contentType === 'video' ? {
  title: videoTitle || 'Título não disponível',
  channel: channelName || 'Canal não disponível', 
  url: mediaUrl || 'URL não disponível'
} : null;

// Criar array de itens com chunks processados
const processedChunks = chunks.map((chunk, index) => {
  const header = createChunkHeader(index, totalChunks, whatsappMessageId, contentAnalysisId, contentType, videoInfo, chunk.has_overlap);

  return {
    chunk_text_with_header: header + chunk.chunk_text,
    chunk_text_raw: chunk.chunk_text,
    chunk_index: chunk.chunk_index,
    chunk_size: chunk.chunk_size,
    total_chunks: totalChunks,
    content_analysis_id: contentAnalysisId,
    whatsapp_message_id: whatsappMessageId,
    original_content_length: fullContent.length,
    has_overlap: chunk.has_overlap,
    overlap_size: chunk.overlap_size || 0,
    chunk_metadata: {
      document_type: originalData.document_type,
      content_type: originalData.content_type,
      agent_type: originalData.agent_type,
      processing_date: new Date().toISOString(),
      group_id: originalData.group_info?.id,
      user_phone: originalData.user_info?.whatsapp,
      video_title: videoTitle,
      channel_name: channelName,
      media_url: mediaUrl,
      has_overlap: chunk.has_overlap,
      overlap_info: chunk.has_overlap ? `${chunk.overlap_size} chars` : null
    }
  };
});

return processedChunks;

// Processar chunks do texto para RAG
const postgresResult = $('INSERT content_analysis (PostgreSQL)').first().json.result;
const originalData = $('Salvar mensagem e preparar para RAG').first().json;

// Extrair o conteúdo completo
const fullContent = originalData.full_content || '';
const contentAnalysisId = postgresResult.content_analysis_id;
const whatsappMessageId = postgresResult.whatsapp_message_id;

// Função para dividir texto em chunks
function createChunks(text, maxChunkSize = 1500) {
  if (!text || text.length === 0) {
    return [{
      chunk_text: 'Conteúdo vazio ou não disponível',
      chunk_index: 0,
      chunk_size: 0
    }];
  }

  const chunks = [];
  const sentences = text.split(/[.!?]+\s+/);
  let currentChunk = '';
  let chunkIndex = 0;

  for (let sentence of sentences) {
    sentence = sentence.trim();
    if (!sentence) continue;

    // Se adicionar a sentença ultrapassar o limite, finaliza o chunk atual
    if (currentChunk.length + sentence.length + 2 > maxChunkSize && currentChunk.length > 0) {
      chunks.push({
        chunk_text: currentChunk.trim(),
        chunk_index: chunkIndex,
        chunk_size: currentChunk.trim().length
      });

      currentChunk = sentence + '. ';
      chunkIndex++;
    } else {
      currentChunk += sentence + '. ';
    }
  }

  // Adiciona o último chunk se houver conteúdo
  if (currentChunk.trim().length > 0) {
    chunks.push({
      chunk_text: currentChunk.trim(),
      chunk_index: chunkIndex,
      chunk_size: currentChunk.trim().length
    });
  }

  return chunks.length > 0 ? chunks : [{
    chunk_text: text.substring(0, maxChunkSize),
    chunk_index: 0,
    chunk_size: Math.min(text.length, maxChunkSize)
  }];
}

// Criar cabeçalho padrão para referência
const createChunkHeader = (chunkIndex, totalChunks, messageId, analysisId) => {
  return `[CHUNK ${chunkIndex + 1}/${totalChunks}] [msg_id:${messageId}] [fulltext_origin_id:${analysisId}]\n\n`;
};

// Processar chunks
const chunks = createChunks(fullContent);
const totalChunks = chunks.length;

// Criar array de itens com chunks processados
const processedChunks = chunks.map((chunk, index) => {
  const header = createChunkHeader(index, totalChunks, whatsappMessageId, contentAnalysisId);

  return {
    chunk_text_with_header: header + chunk.chunk_text,
    chunk_text_raw: chunk.chunk_text,
    chunk_index: chunk.chunk_index,
    chunk_size: chunk.chunk_size,
    total_chunks: totalChunks,
    content_analysis_id: contentAnalysisId,
    whatsapp_message_id: whatsappMessageId,
    original_content_length: fullContent.length,
    chunk_metadata: {
      document_type: originalData.document_type,
      content_type: originalData.content_type,
      agent_type: originalData.agent_type,
      processing_date: new Date().toISOString(),
      group_id: originalData.group_info?.id,
      user_phone: originalData.user_info?.whatsapp
    }
  };
});

return processedChunks;

// Código para nó Code do n8n
// Dados da transcrição (vem do input direto - nó anterior)
const captions = $json.data.transcript.captions;
const videoId = $json.data.videoId;

// Dados do vídeo (nó não conectado - Get - video info retorna array)
const videoInfo = $('Get - video info').first().json;

// Filtrar captions válidos
const validCaptions = captions.filter(caption => 
  caption.text && caption.text.trim() !== "No text"
);

// Formatar transcrição com timestamps
const transcript = validCaptions.map(caption => {
  const totalSeconds = Math.floor(parseFloat(caption.start));
  const minutes = Math.floor(totalSeconds / 60);
  const remainingSeconds = totalSeconds % 60;
  const offset = `${minutes}m${remainingSeconds}s`;
  const text = caption.text.trim();
  return `offset: ${offset} - text: ${text}`;
}).join('\n');

// Criar transcrição limpa (apenas texto, sem timestamps)
const clean_fulltext = validCaptions.map(caption => 
  caption.text.trim()
).join(' ');

// Encontrar maior thumbnail (é 'thumbnail', não 'thumbnails')
let maxThumbnail = "";
if (videoInfo && videoInfo.thumbnail && Array.isArray(videoInfo.thumbnail) && videoInfo.thumbnail.length > 0) {
  const largest = videoInfo.thumbnail.reduce((max, current) => {
    return (current.width * current.height) > (max.width * max.height) ? current : max;
  });
  maxThumbnail = largest.url;
}

// Converter duração de segundos para formato "XmYs"
const lengthSeconds = parseInt(videoInfo.lengthSeconds);
const minutes = Math.floor(lengthSeconds / 60);
const seconds = lengthSeconds % 60;
const formattedLength = `${minutes}m${seconds}s`;

return [{
  video: {
    id: videoId,
    title: videoInfo.title || "No title found",
    description: videoInfo.description || "",
    length: formattedLength,
    channel: {
      title: videoInfo.channelTitle || "",
      id: videoInfo.channelId || ""
    },
    transcript: transcript,
    clean_fulltext: clean_fulltext, // NOVA SAÍDA - texto limpo para BD
    thumbnail: maxThumbnail
  }
}];

