// Extrai e formata a análise de PDF para envio no WhatsApp
// Para usar no n8n: cole este código no nó Code após a resposta da API OpenAI

function formatarMensagemWhatsApp() {
    try {
        // Extrai o JSON da resposta da API OpenAI
        const responseData = $('OpenAI - Análise PDF1').item.json;

        // Caminho para o texto da análise: output[0].content[0].text
        let analiseTexto;

        if (responseData.output && responseData.output[0] && responseData.output[0].content && responseData.output[0].content[0]) {
            analiseTexto = responseData.output[0].content[0].text;
        } else {
            throw new Error("Estrutura de resposta da API não encontrada");
        }

        // Parse do JSON da análise
        const analise = JSON.parse(analiseTexto);

        // Verifica se o documento foi aceito ou rejeitado
        if (analise.status !== "aceito") {
            return formatarDocumentoRejeitado(analise);
        }

        // Formata mensagem completa para documento aceito
        return formatarDocumentoAceito(analise);

    } catch (error) {
        return `❌ *ERRO NO PROCESSAMENTO*\n\nNão foi possível processar a análise do documento.\n\n*Detalhes do erro:* ${error.message}`;
    }
}

function formatarDocumentoRejeitado(analise) {
    return `❌ *DOCUMENTO NÃO ANALISADO*\n` +
           `━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━\n\n` +
           `📄 *Tipo identificado:* ${analise.tipo_documento.replace('_', ' ')}\n\n` +
           `⚠️ *Motivo:* ${analise.resposta_whatsapp.texto_formatado}\n\n` +
           `ℹ️ *Este tipo de documento não é analisado automaticamente pelo sistema.*`;
}

function formatarDocumentoAceito(analise) {
    const { resposta_whatsapp, analise_detalhada, filtros_aplicaveis } = analise;

    let mensagem = "";

    // Cabeçalho principal
    mensagem += `${resposta_whatsapp.emoji_categoria} *ANÁLISE COMPLETA DE DOCUMENTO*\n`;
    mensagem += `━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━\n\n`;

    // Título e autor
    mensagem += `📄 *TÍTULO:*\n${analise_detalhada.titulo}\n\n`;
    mensagem += `👤 *AUTOR(ES):*\n${analise_detalhada.autor}\n\n`;

    // Resumo executivo
    mensagem += `📋 *RESUMO EXECUTIVO:*\n${analise_detalhada.resumo_executivo}\n\n`;

    // Insights principais
    mensagem += `💡 *PRINCIPAIS INSIGHTS:*\n`;
    analise_detalhada.insights_principais.forEach((insight, index) => {
        mensagem += `${index + 1}. ${insight}\n`;
    });
    mensagem += `\n`;

    // Dados relevantes
    mensagem += `📊 *DADOS E NÚMEROS IMPORTANTES:*\n`;
    analise_detalhada.dados_relevantes.forEach((dado) => {
        mensagem += `• ${dado}\n`;
    });
    mensagem += `\n`;

    // Conclusões e recomendações
    mensagem += `🎯 *CONCLUSÕES E RECOMENDAÇÕES:*\n`;
    analise_detalhada.conclusoes.forEach((conclusao, index) => {
        mensagem += `${index + 1}. ${conclusao}\n`;
    });
    mensagem += `\n`;

    // Informações técnicas
    mensagem += `ℹ️ *INFORMAÇÕES TÉCNICAS:*\n`;
    mensagem += `• *Tipo:* ${analise.tipo_documento.replace('_', ' ')}\n`;
    mensagem += `• *Complexidade:* ${filtros_aplicaveis.complexidade.toUpperCase()}\n`;
    mensagem += `• *Tamanho:* ${filtros_aplicaveis.tamanho.toUpperCase()}\n`;
    mensagem += `• *Confiança da classificação:* ${(analise.confianca_classificacao * 100).toFixed(0)}%\n\n`;

    // Tags
    if (filtros_aplicaveis.tags_automaticas && filtros_aplicaveis.tags_automaticas.length > 0) {
        mensagem += `🏷️ *TAGS:*\n`;
        mensagem += filtros_aplicaveis.tags_automaticas
            .map(tag => `#${tag.replace(/\s+/g, '')}`)
            .join(' ');
    }

    return mensagem;
}

// Executa a função e retorna o resultado
const mensagemFormatada = formatarMensagemWhatsApp();

// Retorna o resultado no formato que o n8n espera
return [{
    json: {
        mensagem: mensagemFormatada,
        timestamp: new Date().toISOString(),
        status: "formatado"
    }
}];

