  const hours = String(spTime.getHours()).padStart(2, '0');
  const minutes = String(spTime.getMinutes()).padStart(2, '0');
  const time = `[${hours}:${minutes}]`;

  // Pega o primeiro nome
  const firstName = msg.member_name.split(' ')[0];

  // Processa o conte√∫do
  let content = msg.message || '';

  // Remove aspas extras e decodifica HTML entities
  content = content.replace(/^"|"$/g, '').replace(/&#44;/g, ',');

  // Se for √°udio e tiver transcri√ß√£o, adiciona indicador
  if (msg.type === 'audio' && content) {
    content = `[audio] ${content}`;
  } else if (!content) {
    // Se n√£o tiver conte√∫do, indica o tipo
    if (msg.type === 'image') content = '[imagem]';
    else if (msg.type === 'video') content = '[v√≠deo]';
    else if (msg.type === 'document') content = '[documento]';
    else if (msg.type === 'sticker') content = '[sticker]';
    else if (msg.type === 'audio') content = '[audio sem transcri√ß√£o]';
