// Separar resumos para envio individual
const resultado = $input.first().json.resultado;

if (resultado.status !== 'found' || !resultado.summaries) {
  return [];
}

// Retornar cada resumo como um item separado
return resultado.summaries.map(summary => ({ json: summary }));

// Preparar dados para envio
const resumo = $input.first().json;

// Determinar o destino do envio
let destinoFinal;
if (resumo.destination_type === 'phone' && resumo.destination_value) {
  destinoFinal = resumo.destination_value;
} else {
  // Usar o pr√≥prio grupo como destino padr√£o
  destinoFinal = resumo.chat_id;
}

// Formatar a mensagem final
const dataFormatada = new Date(resumo.summary_date).toLocaleDateString('pt-BR');
const mensagemFinal = `${resumo.summary_content}\n\n_Esse resumo foi gerado por IA_`;

return [{
  json: {
    summary_id: resumo.summary_id,
    group_id: resumo.group_id,
    group_name: resumo.group_name,
    destino: destinoFinal,
    mensagem: mensagemFinal,
    send_attempts: resumo.send_attempts,
    summary_date: resumo.summary_date,
    insance: resumo.instance,
  }
}];

// Verificar se o envio foi bem-sucedido
const dadosEnvio = $('Code - Preparar Envio2').item.json;
const respostaWhatsApp = $input.first().json;

// Determinar se foi sucesso ou falha
let statusEnvio, tentativas;
if (respostaWhatsApp && !respostaWhatsApp.error) {
  statusEnvio = 'sent';
  tentativas = dadosEnvio.send_attempts + 1;
} else {
  statusEnvio = dadosEnvio.send_attempts >= 2 ? 'failed' : 'pending';
  tentativas = dadosEnvio.send_attempts + 1;
}

return [{
  json: {
    summary_id: dadosEnvio.summary_id,
    send_status: statusEnvio,
    send_attempts: tentativas,
    destination_used: dadosEnvio.destino,
    sent_at: statusEnvio === 'sent' ? new Date().toISOString() : null,
    group_name: dadosEnvio.group_name,
    success: statusEnvio === 'sent'
  }
}];

// Log do resultado do envio
const resultado = $input.first().json.resultado;
const dadosEnvio = $('Code - Processar Resultado2').item.json;

if (dadosEnvio.success) {
  console.log('‚úÖ RESUMO ENVIADO COM SUCESSO');
  console.log('Grupo:', dadosEnvio.group_name);
  console.log('ID do resumo:', dadosEnvio.summary_id);
  console.log('Destino:', dadosEnvio.destination_used);
  console.log('Enviado em:', dadosEnvio.sent_at);
} else {
  console.log('‚ùå FALHA NO ENVIO');
  console.log('Grupo:', dadosEnvio.group_name);
  console.log('ID do resumo:', dadosEnvio.summary_id);
  console.log('Tentativas:', dadosEnvio.send_attempts);
  console.log('Status:', dadosEnvio.send_status);
}

return [{ json: dadosEnvio }];
